mapper.update:
update account set bi_update_ts=now() 
     ,app_user_id = #{appUserId}
     ,account_type = #{accountType}
     ,alias_name = #{aliasName}
     ,currency = #{currency}
     ,total_money = #{totalMoney}
     ,available_money = #{availableMoney}
     ,freeze_money = #{freezeMoney}
     ,in_float_money = #{inFloatMoney}
     ,state = #{state}
     ,account_template_id = #{accountTemplateId}
     ,expand_json = #{expandJson}
     ,date_updated = #{dateUpdated}
     ,check_value = #{checkValue}
     ,recharge_count = #{rechargeCount}
     ,recharge_total_amount = #{rechargeTotalAmount}
     ,date_recharge_first = #{dateRechargeFirst}
     ,withdraw_count = #{withdrawCount}
     ,withdraw_total_amount = #{withdrawTotalAmount}
     ,date_user_register = #{dateUserRegister}
     ,fin_subject_code = #{finSubjectCode}
where
id = #{id}

mapper.select:
select

from account t1
where
state != 2
AND app_user_id = #{appUserId}

    AND account_type = #{accountType}
