mapper.update:
update account_sub_loan set bi_update_ts=now() 
     ,app_user_id = #{appUserId}
     ,account_type = #{accountType}
     ,balance_direction = #{balanceDirection}
     ,alias_name = #{aliasName}
     ,account_id = #{accountId}
     ,currency = #{currency}
     ,total_money = #{totalMoney}
     ,available_money = #{availableMoney}
     ,freeze_money = #{freezeMoney}
     ,due_in = #{dueIn}
     ,stay_still = #{stayStill}
     ,stay_interest = #{stayInterest}
     ,make_interest = #{makeInterest}
     ,make_reward = #{makeReward}
     ,overdue = #{overdue}
     ,state = #{state}
     ,account_template_id = #{accountTemplateId}
     ,expand_json = #{expandJson}
     ,check_value = #{checkValue}
     ,date_open = #{dateOpen}
     ,date_close = #{dateClose}
     ,credit_amount = #{creditAmount}
     ,deposit_amount = #{depositAmount}
     ,ctu_amount = #{ctuAmount}
     ,sys_amount = #{sysAmount}
where
id = #{id}

mapper.select:
select

from account_sub_loan t1
where
state != 2
AND t1.app_user_id = #{appUserId}

    AND account_type = #{accountType}
