mapper.update:
        update pano_proj set
        version=version+1
        ,date_updated=now()
        ,sound_src=#{soundSrc}
        ,video_src=#{videoSrc}
        ,map_src=#{mapSrc}
		,category_id=#{categoryId}
		,type=#{type}
		,name=#{name}
		,is_comments=#{isComments}
		,is_planetoid=#{isPlanetoid}
		,is_seccuss=#{isSeccuss}
		,is_fps=#{isFps}
		,snow_type=#{snowType}
		,thumbs_up_num=#{thumbsUpNum}
		,pv_num=#{pvNum}
		,logo_pic_url=#{logoPicUrl}
		,logo_web_url=#{logoWebUrl}
		,pwd=#{pwd}
		,tour_edit_json=#{tourEditJson}
		,ext_cfg_json=#{extCfgJson}
		,state=#{state}
		,memo=#{memo}
		,order_no=#{orderNo}
		,keyword=#{keyword}
		where
        id= #{id} and create_id = #{createId}

mapper.select:
select
from pano_proj t1 where type=#{type}
order by t1.date_created desc

select
from pano_proj t1 where type=#{type}
order by t1.date_created desc

mapper.update:
update pano_proj set
	    version=version+1
	   ,date_updated=now()
	   ,tour_edit_json=#{tourEditJson}
where 
	id= #{id} and create_id = #{createId}

update pano_proj set thumbs_up_num=thumbs_up_num+1
where 
	id= #{id}

update pano_proj set pv_num=pv_num+1
where 
	id= #{id}
