mapper.update:
update pano_scene set
version=version+1
,date_updated=now()
,proj_id=#{projId}
,scene_title=#{sceneTitle}
,logo_url=#{logoUrl}
,scene_src=#{sceneSrc}
,hlookat=#{hlookat}
,vlookat=#{vlookat}
,ext_cfg_json=#{extCfgJson}
,version=#{version}
,state=#{state}
,memo=#{memo}
,order_no=#{orderNo}
,keyword=#{keyword}
where
id= #{id} and create_id = #{createId}

mapper.select:
select

from pano_scene t1
where proj_id=#{projId}


        and t1.del_flag=#{delFlag}


        and t1.del_flag=0


 and t1.create_id = #{createId}

     and DATE_FORMAT(t1.date_created,'%Y-%m-%d')>=DATE_FORMAT(#{dateBegin},'%Y-%m-%d')


     and DATE_FORMAT(t1.date_created,'%Y-%m-%d')<=DATE_FORMAT(#{dateEnd},'%Y-%m-%d')

order by t1.order_no asc
