mapper.select:
select

from org_department t1
where  t1.id = #{id}

mapper.update:
update org_department set
version=version+1
,name=#{name}
,code=#{code}
,parent_id=#{parentId}
,state=#{state}
,memo=#{memo}
,order_no=#{orderNo}
,keyword=#{keyword}
,del_flag=#{delFlag}
,invalid_time=#{invalidTime}
,date_updated=#{dateUpdated}
where  id = #{id}

update org_department set
version=version+1
,update_id=#{updateId}
,update_ip=#{updateIp}
,del_flag=1
where  id = #{id}

update org_department set
version=version+1
,update_id=#{updateId}
,update_ip=#{updateIp}
,del_flag=0
where  id = #{id}
