mapper.select:
select

from t_org_department t1 
where  t1.id = #{id}

mapper.update:
update t_org_department set 
version=version+1
,update_id=#{updateId}
,update_ip=#{updateIp}
,name=#{name}
,code=#{code}
,parentid=#{parentid}
,level=#{level}
,p_code=#{pCode}
,state=#{state}
,memo=#{memo}
,order_no=#{orderNo}
,keyword=#{keyword}
,del_flag=#{delFlag}
,invalid_time=#{invalidTime}
,date_update=#{dateUpdate}
where  id = #{id}

update t_org_department set 
version=version+1
,update_id=#{updateId}
,update_ip=#{updateIp}
,del_flag='1'
where  id = #{id}

update t_org_department set 
version=version+1
,update_id=#{updateId}
,update_ip=#{updateIp}
,del_flag='0'
where  id = #{id}
