mapper.select:
select

from sys_variable t1
where  t1.id = #{id}

mapper.insert:
insert into sys_variable (
	 code
	,name
	,parent_id
	,memo
	,version
	,order_no
	,del_flag
	,create_id
	,date_created
	,date_updated
) values (
	 #{code}
	,#{name}
	,#{parentId}
	,#{memo}
	,0
	,(
	   select t.num from (
		  select (IFNULL(max(order_no),0)+1) as num from sys_variable
		  where


				parent_id=#{parentId}


				parent_id is null or parent_id =''


	   ) t
	)
	,0
	,#{createId}
	,now()
	,now()
)

mapper.update:
update sys_variable set version=version+1,date_updated=now()
,name=#{name}
,code=#{code}
,parent_id=#{parentId}
,order_no=#{orderNo}
where  id = #{id}
