mapper.select:
select

from t_sys_variable t1
where  t1.id = #{id}

mapper.update:
update t_sys_variable set version=version+1
,name=#{name}
,code=#{code}
,parentid=#{parentid}
,level=#{level}
,p_code=#{pCode}
,order_no=#{orderNo}
,keyword=#{keyword}
,invalid_time=#{invalidTime}
,date_update=now()
,update_id=#{updateId}
,update_ip=#{updateIp}
where  id = #{id}

mapper.select:
select ,t2.name pname from t_sys_variable t1 left join t_sys_variable t2 on t1.parentid = t2.id where 1=1 and t1.parentid=#{parentid}
and t1.del_flag=#{delFlag}
and t1.del_flag='0'
order by t1.code

select
from t_sys_variable t1 where 1=1 and t1.parentid=#{parentid}
and t1.del_flag=#{delFlag}
and t1.del_flag='0'
order by t1.code
