mapper.select:
select

from t_sys_variable t1
where  t1.id = #{id}

mapper.update:
update t_sys_variable set version=version+1
,name=#{name}
,code=#{code}
,parent_id=#{parentId}
,order_no=#{orderNo}
,keyword=#{keyword}
,date_updated=now()
where  id = #{id}

mapper.select:
select ,t2.name pname from t_sys_variable t1 left join t_sys_variable t2 on t1.parent_id = t2.id where 1=1 and t1.parent_id=#{parentId}
and t1.del_flag=#{delFlag}
and t1.del_flag=0
order by t1.code

select
from t_sys_variable t1 where 1=1 and t1.parent_id=#{parentId}
and t1.del_flag=#{delFlag}
and t1.del_flag=0
order by t1.code
