mapper.select:
select

from t_auth_role t1 
where  t1.id = #{id}

mapper.update:
update t_auth_role set 
version=version+1
,update_id=#{updateId}
,update_ip=#{updateIp}
,name=#{name}
,is_super=#{isSuper}
,memo=#{memo}
,order_no=#{orderNo}
,keyword=#{keyword}
,del_flag=#{delFlag}
,date_update=#{dateUpdate}
where  id = #{id}

update t_auth_role set 
version=version+1
,update_id=#{updateId}
,update_ip=#{updateIp}
,del_flag='1'
where  id = #{id} and is_super!='1'

update t_auth_role set 
version=version+1
,update_id=#{updateId}
,update_ip=#{updateIp}
,del_flag='0'
where  id = #{id}
