mapper.update:
update pano_proj set
version=version+1
,date_updated=now()
,category_id=#{categoryId}
,type=#{type}
,name=#{name}
,sound_src=#{soundSrc}
,video_src=#{videoSrc}
,is_comments=#{isComments}
,is_planetoid=#{isPlanetoid}
,is_mending=#{isMending}
,map_src=#{mapSrc}
,is_seccuss=#{isSeccuss}
,logo_pic=#{logoPic}
,logo_url=#{logoUrl}
,snow_type=#{snowType}
,is_fps=#{isFps}
,state=#{state}
,memo=#{memo}
,order_no=#{orderNo}
,keyword=#{keyword}
,del_flag=#{delFlag}
where
id= #{id}

mapper.select:
select
from pano_proj t1 where type=#{type}
order by t1.date_created desc

